INSERT INTO `tp_xwdtcontent` (`id`,`state`,`display`,`title`,`shownum`,`editorValue`,`img`,`tdktitle`,`tdkdescription`,`tdkkeywords`,`create_time`,`update_time`,`delete_time`,`isdelete`,`status`) VALUES ('94','8','1','高速公路ETC门架系统专用锂电池UPS电源','23','&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;高速公路专用&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池&lt;/span&gt;UPS&lt;span style=&quot;font-family: 宋体;&quot;&gt;电源。这种锂电池应用于高速公路&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;门架系统、锂电池储能系统。&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池专门为高速公路&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;门架系统服务的，具有短路保护、逆变频率可设置、电池充电管理、监测等多种功能，具有交流自启动及锂电池预充功能，可实现&lt;/span&gt;3&lt;span style=&quot;font-family: 宋体;&quot;&gt;台并机扩容的功能。该系列产品具有高稳定性、高可靠性、高实用性，是一款性价比极高的理想电源。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px; color: rgb(79, 129, 189);&quot;&gt;&lt;a href=&quot;http://www.hb-xdl.cn/xwdtid/9.html&quot;&gt;锂电池&lt;/a&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 16px; font-family: 宋体;&quot;&gt;电源特点：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-size: 19px; font-family: 宋体;&quot;&gt;●&lt;/span&gt;&lt;span style=&quot;font-size: 16px; font-family: 宋体;&quot;&gt;采用&lt;/span&gt;19&lt;span style=&quot;font-size: 16px; font-family: 宋体;&quot;&gt;英寸机架式结构，便于安装及节省空间&lt;/span&gt;;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-size: 19px; font-family: 宋体;&quot;&gt;●&lt;/span&gt;&lt;span style=&quot;font-size: 16px; font-family: 宋体;&quot;&gt;待机损耗小、逆变效率高，节能环保&lt;/span&gt;;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;●具有&lt;/span&gt;AC&lt;span style=&quot;font-family: 宋体;&quot;&gt;正在恢复时自动重启及&lt;a href=&quot;http://www.hb-xdl.cn/xwdtid/9.html&quot; style=&quot;text-decoration: underline; font-size: 16px; font-family: 宋体; color: rgb(79, 129, 189);&quot;&gt;&lt;span style=&quot;font-size: 16px; font-family: 宋体; color: rgb(79, 129, 189);&quot;&gt;锂电池&lt;/span&gt;&lt;/a&gt;预充功能&lt;/span&gt;;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;●纯正弦波输出，可以带各类型的负载&lt;/span&gt;(R&lt;span style=&quot;font-family: 宋体;&quot;&gt;载、&lt;/span&gt;C&lt;span style=&quot;font-family: 宋体;&quot;&gt;载、&lt;/span&gt;L&lt;span style=&quot;font-family: 宋体;&quot;&gt;载&lt;/span&gt;) ;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;●可实现&lt;/span&gt;3&lt;span style=&quot;font-family: 宋体;&quot;&gt;台并机扩容功能&lt;/span&gt;;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;●具有电池防反接保护、过充保护、过载保护、过温保护、输出短路保护等&lt;/span&gt;;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;●冷启动功能。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;目前&lt;/span&gt;Etc&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池被广泛的应用在&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;门架系统，据了解，很多地区的高速都要统一安装该产品，一般情况下该系统是需要电池来服务的。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;高速公路专用&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池&lt;/span&gt;UPS&lt;span style=&quot;font-family: 宋体;&quot;&gt;不间断电源，生产厂家与常规稳压器加&lt;/span&gt;UPS&lt;span style=&quot;font-family: 宋体;&quot;&gt;相比其功率器件的损耗可降低一倍，再以高度集成模块式设计，从而缩小体积，增大功率密度。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;它优异的性能，有一体化的设计，完善的智能控制系统，为各行业用户提供持久的高可靠性电力保护。同时，也避免了用户在购买&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源&lt;/span&gt;UPS&lt;span style=&quot;font-family: 宋体;&quot;&gt;电源时需要考虑的兼容性、场地等问题。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','/tmp/uploads/20191125/5191675fdd7775a802f6027471c5a72c.jpg','高速公路ETC门架系统专用锂电池UPS电源-行业新闻-河北鑫动力鑫能源科技有限公司','','','1574672226','1576462370','0','0','1');
INSERT INTO `tp_xwdtcontent` (`id`,`state`,`display`,`title`,`shownum`,`editorValue`,`img`,`tdktitle`,`tdkdescription`,`tdkkeywords`,`create_time`,`update_time`,`delete_time`,`isdelete`,`status`) VALUES ('95','8','1','ETC收费站使用锂电池已逐渐增长','29','&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;如今，不论是高速公路出入口，还是各大银行营业点，都能看到类似广告。&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;的普及也给&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池带来了巨大的市场空间。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;高速公路&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;收费站的大范围使用，也给其后服务市场带来了巨大的发展空间，以&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源&lt;a href=&quot;http://www.hb-xdl.cn/xwdtid/9.html&quot; style=&quot;text-decoration: underline; font-size: 16px; font-family: 宋体; color: rgb(79, 129, 189);&quot;&gt;&lt;span style=&quot;font-size: 16px; font-family: 宋体; color: rgb(79, 129, 189);&quot;&gt;锂电池&lt;/span&gt;&lt;/a&gt;为例，实现爆发式增长。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;随着&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池的持续增长，给市场的企业提供了良好的发展机会。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;在国际政策大力推动下，&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;突然升温成为新兴的市场，同时也带动&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源&lt;a href=&quot;http://www.hb-xdl.cn/xwdtid/9.html&quot; style=&quot;color: rgb(79, 129, 189); text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-size: 16px; font-family: 宋体; color: rgb(79, 129, 189);&quot;&gt;锂电池&lt;/span&gt;&lt;/a&gt;销量出现巨大的增长。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;这也给一些锂电池企业提供了新的发展机会。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池的优势：&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;1&lt;span style=&quot;font-family: 宋体;&quot;&gt;、减少&lt;/span&gt;UPS&lt;span style=&quot;font-family: 宋体;&quot;&gt;系统的尺寸和重量，更有效地利用空间，&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池能量密度大，能将大量能量储存进更小的空间里，节省空间。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;2&lt;span style=&quot;font-family: 宋体;&quot;&gt;、延长&lt;/span&gt;UPS&lt;span style=&quot;font-family: 宋体;&quot;&gt;使用寿命，降低维护成本，&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池的长寿命设计将大大降低后期更换电池的成本和维护费用。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;3&lt;span style=&quot;font-family: 宋体;&quot;&gt;、&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池不含铅、镍、铭等重金属，是绿色节能环保的首选。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;4&lt;span style=&quot;font-family: 宋体;&quot;&gt;、尽管&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;专用锂电池初始成本仍然比传统电池高，但价格差距在过去几年已明显缩小，&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池解决方案可以提供的预计&lt;/span&gt;.&lt;span style=&quot;font-family: 宋体;&quot;&gt;总成本在保证其设计寿命的基础上节约了&lt;/span&gt;10%&lt;span style=&quot;font-family: 宋体;&quot;&gt;至&lt;/span&gt;40%&lt;span style=&quot;font-family: 宋体;&quot;&gt;。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;目前锂电池&lt;/span&gt;UPS&lt;span style=&quot;font-family: 宋体;&quot;&gt;作为&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源被广泛的应用在&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;门架系统，据了解，很多地区的高速都要统一安装该产品，一般情况下该系&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;统是需要电池来服务的。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','/tmp/uploads/20191125/bc314a9bd96c52a218fa675b822d544d.png','ETC收费站使用锂电池已逐渐增长-行业新闻-河北鑫动力鑫能源科技有限公司','','','1574672402','1576462358','0','0','1');
INSERT INTO `tp_xwdtcontent` (`id`,`state`,`display`,`title`,`shownum`,`editorValue`,`img`,`tdktitle`,`tdkdescription`,`tdkkeywords`,`create_time`,`update_time`,`delete_time`,`isdelete`,`status`) VALUES ('96','8','1','UPS备用电源专用锂电池已应用在智能设备上','31','&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;UPS&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池已大规模应用在&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;智能交通设备上。在&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;收费智能化的今天，没有电力，一切的保障措施均无从说起；而&lt;/span&gt;ETC&lt;span style=&quot;font-size: 16px; font-family: 宋体; color: rgb(31, 73, 125);&quot;&gt;&lt;a href=&quot;http://www.hb-xdl.cn/index.html&quot;&gt;备用电源锂电池&lt;/a&gt;是电力保障的核心。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池已应用在智能交通设备上，随着我国公路建设的高速发展和汽车数量的增加，传统的收费系统难以满足现代发展，而智能交通电子收费系统&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;是解决问题的有效手段之一。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;为解决智能交通&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;系统安全可靠与环境适应性问题，河北鑫动力设计出了&lt;/span&gt;ETC&lt;span style=&quot;font-size: 16px; font-family: 宋体; color: rgb(31, 73, 125);&quot;&gt;&lt;a href=&quot;http://www.hb-xdl.cn/index.html&quot;&gt;备用电源锂电池&lt;/a&gt;&lt;/span&gt;UPS&lt;span style=&quot;font-family: 宋体;&quot;&gt;电源。该电源具有长寿命、高功率输出、安全可靠、宽温工作等特性，尤其适合于户外低温及高温环境使用。高速公路经营单位均衡推进&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;门架建设实施，抓住有利时机，加大督导力度，全面做好取消高速公路，省界收费站各项工作。锂电池将会大规模的投入使用到&lt;/span&gt;Etc&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源中&lt;/span&gt;.&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池&lt;/span&gt;UPS&lt;span style=&quot;font-family: 宋体;&quot;&gt;电源的产品特点。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;1&lt;span style=&quot;font-family: 宋体;&quot;&gt;、可靠性：户外高防护等级，多种电源输入，电池柜与设备柜可分离、电池监控；&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;2&lt;span style=&quot;font-family: 宋体;&quot;&gt;、多柜位：独立设备柜，可部署更多主设备；&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;3&lt;span style=&quot;font-family: 宋体;&quot;&gt;、一体化；集成电源、空调、防雷、电池、监控等所有设备，一体化集成，快速部署；&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;4&lt;span style=&quot;font-family: 宋体;&quot;&gt;、环境适应性：防雷、宽温度范围、宽电源输入；&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;5&lt;span style=&quot;font-family: 宋体;&quot;&gt;、安全性：多种防盗模式、视频监控、主动报警。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;目前，高速公路&lt;/span&gt;&lt;span style=&quot;font-family: Calibri, serif;&quot;&gt;ETC&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;收费系统已覆盖全国&lt;/span&gt;&lt;span style=&quot;font-family: Calibri, serif;&quot;&gt;99%&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;的主线收费站，为广大用户的出行带来了一卡畅行全国的便捷体验。&lt;/span&gt;&lt;span style=&quot;font-family: Calibri, serif;&quot;&gt;ETC&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池&lt;/span&gt;&lt;span style=&quot;font-family: Calibri, serif;&quot;&gt;UPS&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;电源专为高速公路&lt;/span&gt;&lt;span style=&quot;font-family: Calibri, serif;&quot;&gt;ETC&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;门架系统量身定做。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','/tmp/uploads/20191126/d054ca57b3efd33b3f2d362b8ccdaa75.jpg','UPS备用电源专用锂电池已应用在智能设备上-行业新闻-河北鑫动力鑫能源科技有限公司','','','1574758958','1576462348','0','0','1');
INSERT INTO `tp_xwdtcontent` (`id`,`state`,`display`,`title`,`shownum`,`editorValue`,`img`,`tdktitle`,`tdkdescription`,`tdkkeywords`,`create_time`,`update_time`,`delete_time`,`isdelete`,`status`) VALUES ('97','8','1','ETC备用电源需要充电吗? ETC多久充一次电','32','&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;各大银行&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;优惠推出后，直线拉升了&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;用户数量，很多车主收到这免费&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;后，对&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;怎么用并不了解，有的车主就奇怪了，怎么没有&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;充电线，这&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源需要充电吗&lt;/span&gt;?&lt;span style=&quot;font-family: 宋体;&quot;&gt;今天就来聊聊&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;充电的相关问题。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源需要充电吗&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;目前主流使用太阳能&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;电子标签，其内部含有&lt;/span&gt;18650&lt;span style=&quot;font-family: 宋体;&quot;&gt;锂电池，外部使用太阳能充电板供电，无需担心&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;没电了怎么办的问题。那么&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;太阳能能用多久&lt;/span&gt;? ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池正常使用&lt;/span&gt;3&lt;span style=&quot;font-family: 宋体;&quot;&gt;年，在&lt;/span&gt;2&lt;span style=&quot;font-family: 宋体;&quot;&gt;年保修期内出现任何质量问题，可以将&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;设备带到&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;办理网点或&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;服务网点检修&lt;/span&gt;/&lt;span style=&quot;font-family: 宋体;&quot;&gt;更换。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;对于太阳能&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;没电了怎么办，其实可以使用&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;充电器为其充电，对于因&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;设备内部电池使用寿命结束的，需要更换新的&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池，当然也可以一直使用&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;充电线为其充电，提供应急使用，事后最好更换&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;多久充一次电？使用说明书上没有严格定义&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;多久充一次电，对于不经常使用&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池的用户，建议每&lt;/span&gt;3&lt;span style=&quot;font-family: 宋体;&quot;&gt;个月为&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源充电一次，避免因&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;锂电池自放电而影响使用寿命。对于使用有线供电的&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;长期不使用，也建议每月充电一次。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','/tmp/uploads/20191126/71e53f730b3f9b9cca3933d5f3f8c338.jpg','ETC备用电源需要充电吗? ETC多久充一次电-行业新闻-河北鑫动力鑫能源科技有限公司','','','1574759062','1576462338','0','0','1');
INSERT INTO `tp_xwdtcontent` (`id`,`state`,`display`,`title`,`shownum`,`editorValue`,`img`,`tdktitle`,`tdkdescription`,`tdkkeywords`,`create_time`,`update_time`,`delete_time`,`isdelete`,`status`) VALUES ('98','9','1','高速ETC门架系统备用电源锂电池','16','&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;对于锂电池的应用场景来说，小编在前几篇文章中就有过详细的介绍，接下来小编就不再多说。本篇文章要介绍的是备用电源&lt;a href=&quot;http://www.hb-xdl.cn/cgalcon/13.html&quot;&gt;锂电池&lt;/a&gt;应用在&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;门架系统中，一般来说，该种备用电源锂电池的规格为&lt;/span&gt;48&lt;span style=&quot;font-family: 宋体;&quot;&gt;伏&lt;/span&gt;50&lt;span style=&quot;font-family: 宋体;&quot;&gt;安时或者&lt;/span&gt;48&lt;span style=&quot;font-family: 宋体;&quot;&gt;伏&lt;/span&gt;100&lt;span style=&quot;font-family: 宋体;&quot;&gt;安时，两种规格的电池放电倍率在&lt;/span&gt;0.5&lt;span style=&quot;font-family: 宋体;&quot;&gt;℃，充电在&lt;/span&gt;0.2&lt;span style=&quot;font-family: 宋体;&quot;&gt;℃到&lt;/span&gt;0.5&lt;span style=&quot;font-family: 宋体;&quot;&gt;℃，至于温度控制方面属于常规的&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源锂电池的温度。就价格来说大概在一元多一瓦时，具体准确的售价方面还需要根据当时的负载或者应用场景进行确定的。有朋友问，高速&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;备用电源&lt;a href=&quot;http://www.hb-xdl.cn/cgalcon/13.html&quot;&gt;锂电池&lt;/a&gt;的参数有哪些？就此小编准备了部分的参数及报价，下面请看高速&lt;/span&gt;ETC&lt;span style=&quot;font-family: 宋体;&quot;&gt;门架收费系统锂电池的参数有哪些的内容。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:37px&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;table&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td width=&quot;187&quot; valign=&quot;top&quot; style=&quot;border: 1px solid windowtext; padding: 0px 7px;&quot;&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-size: 19px; font-family: 宋体;&quot;&gt;产品型号&lt;/span&gt;&lt;span style=&quot;font-size: 19px;&quot;&gt;:&lt;/spa