INSERT INTO `tp_xwdtcontent` (`id`,`state`,`display`,`title`,`shownum`,`editorValue`,`img`,`tdktitle`,`tdkdescription`,`tdkkeywords`,`create_time`,`update_time`,`delete_time`,`isdelete`,`status`) VALUES ('109','8','1','电动摩托车锂电池，放心使用','65','&lt;p style=&quot;text-indent:32px&quot;&gt;&lt;span style=&quot;font-family: Arial;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;现如今，市面上的&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;电动摩托车锂电池&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: Arial;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;是越来越多；而且随着新能源、环保大风这几年的猛吹；以及之前雾霾对大家日常生活的侵扰。使得许多人的环保理念，&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;电动摩托车锂电池&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: Arial;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在短短的两三年之间达到了一个较高的水准。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:32px&quot;&gt;&lt;span style=&quot;font-family: Arial;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:32px&quot;&gt;&lt;span style=&quot;font-family: Arial;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在这之前，燃油摩托车一直都是市场主流，虽然电动的蜂潮在汽车界吹得比较迅猛，但在摩托车上好像总是有那么些距离。但现在不同了，&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(