INSERT INTO `tp_xwdtcontent` (`id`,`state`,`display`,`title`,`shownum`,`editorValue`,`img`,`tdktitle`,`tdkdescription`,`tdkkeywords`,`create_time`,`update_time`,`delete_time`,`isdelete`,`status`) VALUES ('112','8','1','电动摩托车锂电池制作优点','58','&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;1.&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;电动摩托车锂电池&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;的优点表现在容量大、工作电压高。容量为同等镍镉电池的两倍，更能适应长时间的通讯联络，而通常的单体锂电池的电压为&lt;/span&gt;3.7V&lt;span style=&quot;font-family:宋体&quot;&gt;是镍镉、镍氢电池的&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;3&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;倍。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:0;text-indent:0&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;2.&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;电动摩托车锂电池&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;荷电保持能力强，允许工作温度范围宽。在（&lt;/span&gt;20&lt;span style=&quot;font-family:宋体&quot;&gt;&plusmn;&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;5&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;）℃下，以开路形式储存&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;30&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;天后，电池的常温放电容量大于额定容量的&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;85%&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;。&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;