INSERT INTO `tp_xwdtcontent` (`id`,`state`,`display`,`title`,`shownum`,`editorValue`,`img`,`tdktitle`,`tdkdescription`,`tdkkeywords`,`create_time`,`update_time`,`delete_time`,`isdelete`,`status`) VALUES ('114','8','1','电动摩托车锂电池和铅酸电池的区别','28','&lt;p style=&quot;;text-align:justify;text-justify:inter-ideograph&quot;&gt;&lt;span style=&quot;font-family: Arial; color: rgb(25, 25, 25); letter-spacing: 0px; background: rgb(255, 255, 255);&quot;&gt;电池&lt;/span&gt;&lt;span style=&quot;font-family: 宋体; color: rgb(25, 25, 25); letter-spacing: 0px; background: rgb(255, 255, 255);&quot;&gt;作为&lt;/span&gt;&lt;span style=&quot;font-family: Arial; color: rgb(25, 25, 25); letter-spacing: 0px; background: rgb(255, 255, 255);&quot;&gt;电动&lt;/span&gt;&lt;span style=&quot;font-family: 宋体; color: rgb(25, 25, 25); letter-spacing: 0px; background: rgb(255, 255, 255);&quot;&gt;摩托&lt;/span&gt;&lt;span style=&quot;font-family: Arial; color: rgb(25, 25, 25); letter-spacing: 0px; background: rgb(255, 255, 255);&quot;&gt;车上的动力来源，&lt;/span&gt;&lt;span style=&quot;font-family: 宋体; color: rgb(25, 25, 25); letter-spacing: 0px; background: rgb(255, 255, 255);&quot;&gt;是消费者选购时的重要参考因素。常用的电池类型有&lt;/span&gt;&lt;a href=&quot;http://www.hb-xdl.cn/index.html&quot;&gt;&lt;span style=&quot;font-family: 宋体; color: rgb(0, 0, 255); letter-spacing: 0px; background: rgb(255, 255, 255);&quot;&gt;电动摩托车锂电池&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-family: 宋体; color: rgb(25, 25, 25); letter-spacing: 0px; background: rgb(255, 255, 255);&quot;&gt;和铅酸电池。那么两者有哪些区别呢？&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;;text-align:justify;text-justify:inter-ideograph&quot;&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;1、&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;安全性。电动摩托车锂电池的发展时间较铅酸电池短，不同的锂电池材料&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: Arial;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;不同&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;，&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;没有统一的标准，所以&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: Arial;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;安全性有所差别&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;。&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: Arial;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;铅酸电池&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;的&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: Arial;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;技术&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;发展已经&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: Arial;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;相当成熟，安全性&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;较高。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;;text-align:justify;text-justify:inter-ideograph&quot;&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;2、&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;电池比能量。电动摩托车锂电池轻便，小巧，能量比高出铅酸电池&lt;/span&gt;40%&lt;span style=&quot;font-family:宋体&quot;&gt;左右。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;http://7j1y9l.com1.z0.glb.clouddn.com/image/k4kx4y5k_63ja5s2lg9s05e0301e1c3f61.png&quot; title=&quot;微信图片_20191015094905副本.png&quot; alt=&quot;微信图片_20191015094905副本.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;电动摩托车锂电池&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;;text-align:justify;text-justify:inter-ideograph&quot;&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;3、&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;使用寿命。铅酸电池的循环充放电次数大约是&lt;/span&gt;500&lt;span style=&quot;font-family:宋体&quot;&gt;次，使用不当易出现硫化，缩短使用寿命。而电动摩托车锂电池的磷酸铁锂电池循环寿命能达到&lt;/span&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;1500&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;次，而且没有记忆效应。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;;text-align:justify;text-justify:inter-ideograph&quot;&gt;&lt;span style=&quot;font-family: Arial;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;4、&lt;/span&gt;&lt;span style=&quot;font-family: Arial;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;自放电率&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;。&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;电动摩托车锂电池一个月内的自放电小于电池电量的&lt;/span&gt;3%&lt;span style=&quot;font-family:宋体&quot;&gt;，而铅酸电池放电是&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: Arial;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;15~30%&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;;text-align:justify;text-justify:inter-ideograph&quot;&gt;&lt;span style=&quot;font-family: Arial;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;5、&lt;/span&gt;&lt;span style=&quot;font-family: Arial;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;font-family:Arial&quot;&gt;放电特性&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;color: rgb(25, 25, 25);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 